mapper.select:
SELECT
COUNT(*)
FROM sso_login_log


        AND username LIKE CONCAT(#{usernameLike,jdbcType=VARCHAR},'%')


        AND status = #{status,jdbcType=INTEGER}


        AND login_time  >=  #{loginStartTime,jdbcType=BIGINT}


        AND login_time  <=  #{loginEndTime,jdbcType=BIGINT}

SELECT

FROM sso_login_log


        AND username LIKE CONCAT(#{usernameLike,jdbcType=VARCHAR},'%')


        AND status = #{status,jdbcType=INTEGER}


        AND login_time  >=  #{loginStartTime,jdbcType=BIGINT}


        AND login_time  <=  #{loginEndTime,jdbcType=BIGINT}


ORDER BY id desc
LIMIT #{startRow}, #{pageSize}
