mapper.select:
SELECT

FROM sso_menu
WHERE del_flag=0
AND sys_code = #{sysCode,jdbcType=VARCHAR}
AND menu_name = #{menuName,jdbcType=VARCHAR}
LIMIT 1

SELECT DISTINCT
FROM sso_menu a LEFT JOIN sso_role_menu b ON a.menu_id = b.menu_id LEFT JOIN sso_role c ON b.role_id = c.role_id LEFT JOIN sso_user_role d ON b.role_id = d.role_id WHERE a.del_flag = 0 AND c.del_flag = 0 AND c.`status` = 0 AND a.`status` = 0 AND a.sys_code = #{sysCode,jdbcType=VARCHAR} AND d.sys_code = #{sysCode,jdbcType=VARCHAR} AND d.user_id = #{userId,jdbcType=BIGINT}
OR (a.sys_code = #{sysCode,jdbcType=VARCHAR} AND a.del_flag = 0 AND a.`status` = 0 and a.use_type=1 ) ORDER BY a.sort_num

SELECT
COUNT(*)
FROM sso_menu


        AND sys_code = #{sysCode,jdbcType=VARCHAR}


        AND menu_parent_id = #{menuParentId,jdbcType=BIGINT}


        AND menu_id != #{excludeMenuId,jdbcType=BIGINT}


        AND menu_name = #{menuName,jdbcType=VARCHAR}


        AND menu_name LIKE CONCAT(#{menuNameLike,jdbcType=VARCHAR},'%')


        AND status = #{status,jdbcType=INTEGER}


        AND del_flag = #{delFlag,jdbcType=INTEGER}

SELECT

FROM sso_menu


        AND sys_code = #{sysCode,jdbcType=VARCHAR}


        AND menu_parent_id = #{menuParentId,jdbcType=BIGINT}


        AND menu_id != #{excludeMenuId,jdbcType=BIGINT}


        AND menu_name = #{deptName,jdbcType=VARCHAR}


        AND menu_name LIKE CONCAT(#{menuNameLike,jdbcType=VARCHAR},'%')


        AND status = #{status,jdbcType=INTEGER}


        AND del_flag = #{delFlag,jdbcType=INTEGER}


ORDER by sort_num

SELECT count(*) FROM sso_menu
WHERE del_flag = 0
AND sys_code = #{sysCode,jdbcType=VARCHAR}
