mapper.select:
SELECT
COUNT(*)
FROM  sso_online_user a
INNER JOIN sso_user b on a.user_id=b.user_id


        AND a.expire_time  >=  #{expireStartTime,jdbcType=BIGINT}


        AND b.username LIKE CONCAT(#{usernameLike,jdbcType=VARCHAR},'%')


        AND b.phone LIKE CONCAT(#{phoneLike,jdbcType=VARCHAR},'%')


        AND a.login_time  >=  #{loginStartTime,jdbcType=BIGINT}


        AND a.login_time  <=  #{loginEndTime,jdbcType=BIGINT}

SELECT a.request_id, a.user_id, b.username, b.nick_name, b.real_name, b.phone, a.source_ip, a.source_address, a.browser_name, a.operate_system, a.login_time, a.login_out_time, a.expire_time FROM sso_online_user a INNER JOIN sso_user b on a.user_id=b.user_id
AND a.expire_time >= #{expireStartTime,jdbcType=BIGINT}
AND b.username LIKE CONCAT(#{usernameLike,jdbcType=VARCHAR},'%')
AND b.phone LIKE CONCAT(#{phoneLike,jdbcType=VARCHAR},'%')
AND a.login_time >= #{loginStartTime,jdbcType=BIGINT}
AND a.login_time <= #{loginEndTime,jdbcType=BIGINT}
ORDER BY a.login_time desc LIMIT #{startRow}, #{pageSize}

mapper.update:
UPDATE sso_online_user
SET
 expire_time = #{expireTime,jdbcType=BIGINT}
 WHERE request_id = #{requestId,jdbcType=VARCHAR}

UPDATE sso_online_user
SET
 expire_time = #{expireTime,jdbcType=BIGINT},
 login_out_time = #{loginOutTime,jdbcType=BIGINT}
WHERE request_id = #{requestId,jdbcType=VARCHAR}
