SELECT
 role_key
FROM
 sso_role a
INNER JOIN sso_user_role b ON a.role_id = b.role_id
WHERE a.del_flag = 0 AND a.`status` = 0
AND a.sys_code = #{sysCode,jdbcType=VARCHAR}
AND b.sys_code = #{sysCode,jdbcType=VARCHAR}
AND b.user_id = #{userId,jdbcType=BIGINT}

