mapper.select:
SELECT

FROM sso_user_dept
WHERE sys_code = #{sysCode,jdbcType=VARCHAR}
AND user_id = #{userId,jdbcType=BIGINT}
LIMIT 1

mapper.delete:
DELETE FROM sso_user_dept
WHERE sys_code = #{sysCode,jdbcType=VARCHAR}
AND user_id = #{userId,jdbcType=BIGINT}

DELETE FROM sso_user_dept
WHERE  sys_code = #{sysCode,jdbcType=VARCHAR}
AND  dept_id = #{deptId,jdbcType=BIGINT}
