mapper.select:
SELECT
role_id
FROM sso_user_role
WHERE sys_code = #{sysCode,jdbcType=VARCHAR}
AND user_id = #{userId,jdbcType=BIGINT}

SELECT a.user_id userId, GROUP_CONCAT(b.role_id) roleIds, GROUP_CONCAT(b.role_name) roleNames FROM sso_user_role a INNER JOIN sso_role b ON a.role_id=b.role_id AND b.del_flag=0 WHERE a.sys_code = #{sysCode,jdbcType=VARCHAR} AND a.user_id in
#{userId}
GROUP BY a.user_id

SELECT count(*) as count ,role_id as roleId FROM sso_user_role WHERE role_id in
#{roleId}
GROUP BY role_id

mapper.delete:
DELETE FROM sso_user_role
WHERE sys_code = #{sysCode,jdbcType=VARCHAR}
AND user_id = #{userId,jdbcType=BIGINT}

DELETE FROM sso_user_role
WHERE role_id = #{roleId,jdbcType=BIGINT}
AND user_id = #{userId,jdbcType=BIGINT}

mapper.update:
UPDATE sso_user_role
SET
 sys_code = #{sysCode,jdbcType=VARCHAR},
 user_id = #{userId,jdbcType=BIGINT},
 role_id = #{roleId,jdbcType=BIGINT}
WHERE id = #{id,jdbcType=BIGINT}
